import * as React from 'react';
import {Text, View, StyleSheet} from 'react-native';
import {IonButton, IonCheckbox, IonIcon, IonInput, IonItem, IonLabel, IonList, IonToggle} from "@ionic/react";
import {lockClosed, logoFacebook, logoGoogle, person} from "ionicons/icons";

import selfEliteName from '../../assets/selfelite.png'

export default function LoginScreen() {
    return (
        <View style={styles.container}>
            <View style = {styles.header}>
                <View style = {styles.headerImg}>
                    <img src = {selfEliteName}/>
                </View>
            </View>
            <View style = {styles.contentStyle}>
                <View style ={{padding: '5rem', paddingBottom: '.5 rem', display: 'flex', flexDirection: 'column'}}>
                    <h1>
                        Welcome
                    </h1>
                    <span>
                        Don't have an account?
                    </span>
                    <span>
                            <i>Register Now!</i>
                    </span>
                </View>
                <View style = {{padding: '.5rem'}}>
                    <IonLabel postition = {'stacked'}> Email ID </IonLabel>
                    <IonItem >
                            <IonIcon slot = {'start'} icon = {person}/>
                            <IonInput value = '' type = {'email'}/>
                    </IonItem>
                </View>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    container: {
        display: 'flex',
        flexDirection: 'column'
    },
    header: {
        display: 'flex',
        justifyContent: 'center'
    },
    headerImg:{
        height: 170,
        display: 'flex',
        justifyContent: 'center',
    },
    contentStyle:{
        flexGrow: 1,
        padding: 1,
        borderTopLeftRadius: '50px',
        borderTopRightRadius: '50px',
        //background: '#f8f8f8',
    },
    loginInput: {
        borderColor: '#838383',
        borderStyle: 'solid',
        borderWidth: 1,
        borderRadius: 6
    }


})
