import * as React from 'react';
import {View, Text} from 'react-native';
import { Provider } from 'react-native-paper'
import { NavigationContainer } from '@react-navigation/native';
import { createStackNavigator } from '@react-navigation/stack'
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs'; 
import { theme } from './core/theme';
import { IonIcon } from '@ionic/react';

//screens
import GroceryList from './navigation/screens/GroceryList';
import Inventory from './navigation/screens/Inventory';
import Settings from './navigation/screens/Settings';
import StartScreen from './navigation/screens/StartScreen';
import LoginScreen from './navigation/screens/LoginScreen';
import RegisterScreen from './navigation/screens/RegisterScreen';


const Tab = createBottomTabNavigator();
const Stack = createStackNavigator();

  function Home() {
    return(
      <Tab.Navigator
                initialRouteName={'Grocery'}
                           screenOptions={({route}) => ({
                               tabBarIcon: ({focused, color, size}) => {
                                   let iconName;
                                   let rn = route.name;

                                   if (rn === 'Grocery')
                                   {
                                       iconName = focused ? 'list' : 'list-outline'; //icons in package. Change later.
                                   }
                                   else if (rn === calendarName)
                                   {
                                       iconName = focused ? 'calendar' : 'calendar-outline'
                                   }
                                   return <Ionicons name={iconName} size={30} color={color}/>
                               },
                               tabBarActiveTintColor: '#55BCF6',
                               tabBarInactiveTintColor: 'grey',
                               tabBarShowLabel: false, //can add or remove the label
                               tabBarStyle:
                                   {
                                       padding: 0,
                                       margin: 10,
                                       height: 60,
                                       borderRadius: 50,
                                       borderWidth: 0,
                                       borderColor: '#000000',
                                       position: 'absolute',
                                       shadowRadius: 1,
                                       shadowOpacity: 50

                                   },
                               tabBarLabelStyle: {padding: 10},
                           })}>
            <Tab.Screen name={'Grocery'} component={GroceryList} />
            <Tab.Screen name={'Inventory'} component={Inventory} />
            <Tab.Screen name={'Settings'} component={Settings} />
        </Tab.Navigator>
    )
  }

export default function App() {
  return (
    <Provider theme={theme}>
        <NavigationContainer>
        <Stack.Navigator
        initialRouteName = "StartScreen"
        screenOptions = {{
          headerShown: false,
        }}
        >
          <Stack.Screen name="StartScreen" component={StartScreen} />
          <Stack.Screen name="LoginScreen" component={LoginScreen} />
          <Stack.Screen name="RegisterScreen" component={RegisterScreen} />
          <Stack.Screen name="Home" component={Home}/>
        </Stack.Navigator>
      </NavigationContainer>
    </Provider> 
  );
}
