import React, { forwardRef, useCallback, useState, useEffect } from 'react'
import {
  StyleSheet,
  Dimensions,
  View,
  Text,
  TouchableOpacity,
  BackHandler,
} from 'react-native'
import BottomSheet, { BottomSheetBackdrop } from '@gorhom/bottom-sheet'
import { Portal } from '@gorhom/portal'
import { Button, TextInput } from 'react-native-paper'
import { ScrollView } from 'react-native-gesture-handler'

const dims = Dimensions.get('window')

const AddNewItemBottomSheet = forwardRef((props, ref) => {
  const [quantity, setQuantity] = useState('')
  const [currentIndex, setCurrentIndex] = useState(-1)
  const handleBackPress = useCallback(() => {
    // console.log('back pressed', currentIndex);
    if (currentIndex > -1) {
      props.onClose()
      return true
    }
    return false
  }, [currentIndex])

  useEffect(() => {
    const sub = BackHandler.addEventListener(
      'hardwareBackPress',
      handleBackPress
    )
    return sub.remove
  }, [currentIndex])

  const renderBackdrop = useCallback(
    (props) => <BottomSheetBackdrop {...props} disappearsOnIndex={-1} />,
    []
  )

  return (
    <Portal>
      <BottomSheet
        ref={ref}
        index={-1}
        snapPoints={['55%']}
        enablePanDownToClose={true}
        animateOnMount
        backdropComponent={renderBackdrop}
        containerHeight={dims.height}
        backgroundStyle={{ backgroundColor: '#fff' }}
        onChange={setCurrentIndex}
      >
        <ScrollView
          style={{
            padding: 16,
          }}
        >
          <Text style={{ color: '#333', fontWeight: 'bold', fontSize: 19 }}>
            New Item
          </Text>
          <View style={{ flex: 1, paddingTop: 24 }}>
            <TextInput placeholder="Item Name" />
            <View
              style={{
                flexDirection: 'row',
                width: '100%',
                marginTop: 16,
                justifyContent: 'center',
              }}
            >
              <TouchableOpacity
                style={styles.quantityButton}
                onPress={() => props?.onClose()}
              >
                <Text style={styles.quantityButtonText}>-</Text>
              </TouchableOpacity>
              <TextInput
                placeholder="Quantity"
                value={quantity.toString()}
                style={{ width: '60%', marginHorizontal: 16 }}
              />
              <TouchableOpacity
                style={styles.quantityButton}
                onPress={() => props?.onClose()}
              >
                <Text style={styles.quantityButtonText}>+</Text>
              </TouchableOpacity>
            </View>
          </View>
          <Button
            mode="contained"
            color="#279AF1"
            onPress={() => console.log('Pressed')}
            style={{ marginTop: 70 }}
          >
            Add
          </Button>
        </ScrollView>
      </BottomSheet>
    </Portal>
  )
})

const styles = StyleSheet.create({
  okButton: {
    marginTop: 10,
    padding: 10,
    backgroundColor: '#279AF1',
    borderRadius: 4,
    justifyContent: 'center',
    alignItems: 'center',
  },
  okButtonText: {
    color: '#fff',
    fontSize: 14,
    justifyContent: 'center',
    alignItems: 'center',
  },

  quantityButton: {
    flex: 1,
    backgroundColor: '#279AF1',
    borderRadius: 4,
    justifyContent: 'center',
    alignItems: 'center',
  },
  quantityButtonText: {
    color: '#fff',
    fontSize: 25,
    justifyContent: 'center',
    alignItems: 'center',
  },
})

export default AddNewItemBottomSheet
